import React from 'react';
import { Card, ListGroup } from 'react-bootstrap'
import TaskModal from './TaskModal.js'

class AllTasks extends React.Component {
  constructor(){
    super();
    this.state = {
      isModalHidden: true,
      task: {}
    }
  }
  render(){
    const { title, tasks, openModal, style } = this.props

    return(
      <Card className='mb-2'>
        <TaskModal task={this.state.task} show={!this.state.isModalHidden}/>
        <Card.Header style={style}>
          <div className="float-left">
            {title} <i className="fa fa-fw fa-th" style={{ fontSize: '1em', marginLeft: 5 }} />
          </div>
          {title !== "Completed Tasks" ?
            <div className="float-right" onClick={openModal}>
              <strong>New</strong><i className="fa fa-fw fa-plus-square" style={{ fontSize: '1em', marginLeft: 5 }} />
            </div>
            : null
          }
        </Card.Header>
        {
          tasks.map(task =>
            <div>
              <ListGroup.Item key={`task-item-${task.id}`} onClick={() => this.setState({task: task, isModalHidden: false})}>{task.title}</ListGroup.Item>
            </div>
          )
        }
      </Card>
    )
  }
}

export default AllTasks;
