import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";



export default class NewProjectModal extends React.Component {
  constructor(props){
    super();

    this.state = {
      show: props.show,
      date: ""
    }
  }

  handleDate = date => (
    this.setState({
      date: date
    })
  )

  handleChange = date => (
    this.setState({
      date: date
    })
  )

  render(){
    return(
      <Modal show={this.props.show}>
        <Modal.Header>
          <Modal.Title>New Project Form</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
          <Form.Group as={Row} controlId="formProName">
              <Form.Label column sm="2">
                <small>Name:</small>
              </Form.Label>
              <Col sm="10">
                <Form.Control plaintext defaultValue="My Cool Project." />
              </Col>
            </Form.Group>
            <Form.Group as={Row} controlId="formProDesc">
                <Form.Label column sm="2">
                  <small>Description:</small>
                </Form.Label>
                <Col sm="10">
                  <Form.Control plaintext defaultValue="I wanna do this and this and this project." />
                </Col>
              </Form.Group>
              <Form.Group as={Row} controlId="formProPriority">
                <Col sm='2'>
                  <Form.Label><small>Priority:</small></Form.Label>
                </Col>
                <Col sm='10'>
                    <Form.Control as="select">
                      <option>Low</option>
                      <option>Medium</option>
                      <option>High</option>
                    </Form.Control>
                </Col>
              </Form.Group>
              <Form.Group as={Row} controlId="formProDeadline">
              <Col sm='2'>
                <Form.Label>
                  <small>Deadline:</small>
                </Form.Label>
              </Col>
              <Col sm='10'>
                <DatePicker
                  onChange={this.handleChange}
                  selected={this.state.date}
                  onChange={this.handleChange}
                  showTimeSelect
                />
              </Col>
              </Form.Group>
          </Form>
        </Modal.Body>

        <Modal.Footer>
          <Button size='sm' variant="secondary" onClick={this.props.handleModalClose}>Close</Button>
          <Button size='sm' variant="primary">Create Project</Button>
        </Modal.Footer>
      </Modal>
    )
  }
}
