import React from 'react'
import { Modal, Button, Form, Col, Row, Accordion, ListGroup, useAccordionToggle } from 'react-bootstrap'
import ChecklistService from '../../services/ChecklistService.js'

function CustomToggle({ children, eventKey, color }) {
  const changeContent = useAccordionToggle(eventKey, () =>
    console.log('works!'),
  )

  return (
    <button
      type="button"
      style={{ backgroundColor: color, color: '#fff', border: 0 }}
      onClick={changeContent}
    >
      {children}
    </button>
  )
}

export default class TaskModal extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      show: props.show,
      task: props.task,
      checklists: [],
    }
    this.checklistService = new ChecklistService(this)
  }

  componentDidMount(){
    this.checklistService.fetchChecklists(this.state.task.id)
  }

  render(){
    return(
      <Modal show={this.state.show}>
        <Modal.Header>
          <div className='float-left'>
            {this.state.task.title}
          </div>
          <div className='float-right' onClick={() => this.setState({show: !this.state.show})}>
            <i className="fa fa-fw fa-times-circle" style={{ fontSize: '1.5em'}} />
          </div>
        </Modal.Header>
        <Modal.Body>
          <Accordion defaultActiveKey='0'>
          {
            this.state.checklists.map(checklist => {
              return(
            <div>
              <CustomToggle eventkey={checklist.id}>
                <ListGroup.Item active>
                  {checklist.title}
                </ListGroup.Item>
              </CustomToggle>
              <Accordion.Collapse eventkey={checklist.id}>
                {checklist.items.map(item => {
                  return(
                    <ListGroup.Item>
                      <div className='float-left'>
                        {item.text}
                      </div>
                      <div className='float-right' onClick={console.log(item.id + ' ' + checklist.id)}>
                        {item.is_completed ? <i className="fa fa-fw fa-check" style={{ fontSize: '1.5em', color: 'silver'}} /> : <i className="fa fa-fw fa-green" style={{ fontSize: '1.5em', color: 'green'}} />}
                      </div>
                    </ListGroup.Item>
                  )
                })}
              </Accordion.Collapse>
            </div>
              )
          })
        }
        </Accordion>
      </Modal.Body>
    </Modal>
    )
  }
}
