class ProjectService {
  constructor (component) {
    this.component = component
  }

  fetchProjects = (userId) => {
    fetch(`https://arcane-sands-50858.herokuapp.com/users/${userId}/projects`)
    .then(r => r.json())
    .then(r => {
      this.component.setState({projects: r})
    })
  }
}
